css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("hardware.fr") {
  #main_top_second,
  #main_top{
    height: auto;
  }
  
  #main_top_second{
    margin-bottom: 10px;
  }
  
  #navigation{
    background-image: none;
    background-color: #42679D;
    height: auto;
  }
  
  #navigation .sous_menu{
    background-color: #F7F7F7;
    padding-bottom: 10px;
  }
  
  #navigation .menu ul li>div{
    background-image: none;
  }
  
  #navigation .menu ul li>div.news_over,
  #navigation .menu ul li>div.dossiers_over,
  #navigation .menu ul li>div.prix_over,
  #navigation .menu ul li>div.forum_over,
  #navigation .menu ul li>div.base_over,
  #navigation .menu ul li>div.pc_over{
   background-color: #F7F7F7;
  }
  
  #banniere_head,
  #nuage_de_tags{
    display: none;
  }
  
  #recherche_ok{
    display: none;
  }
  
  #categorie_navigation #recherche_champ{
    background-image: none;
  }
  
  #categorie_navigation #recherche_champ input.keyword{
    height: 14px;
    background-color: #E4E3E3;
  }
  
  #header #utilisateur{
    background-color: #F7F7F7;
    padding: 5px 0 5px 0;
    margin-right: 0;
  }
  
  #header #utilisateur,
  #header #utilisateur #left,
  #header #utilisateur #right{
    background-image: none;
  }
  
  #main_content_pages,
  #main_content_grey,
  #main_content_white{
    border: none;
    width: 100%;
  }
  
  #main_right{
    display: none;
  }
  
  #main_content_pages{
    width: 100%;
  }
  
  #actualites{
    width: 65%;
  }
  
  #a_la_une{
    width: 32%;
  }
  
  #actualites_block li .date_titre{
    width: 100%;
  }
  
  #communaute,
  #dossier_blogs{
    width: 98%;
  }
  
  #dossier_blogs .dossiers{
    width: 88%;
  }
  
  #dossier_blogs .dossiers_produits{
    width: 10%;
  }
  
  #footer #copyright{
    width: 100%;
    padding-bottom: 10px;
  }
  
  #footer #footerlink{
    width: 100%;
    margin-top: 10px;
  }
}
