css:
/* STYLE BY UNHOLYMASTER || TUMBLR & INSTAGRAM */

@-moz-document
domain("ancestry.com"),
domain("ancestry.com.au") {

#personCard .widgtContnt .childrenitems td {
color: #a2a0a0 !important;
}

#personCard .widgtContnt .personInfoContainer, #results-main tr.tblrowalt td {
background: #0a0a0a;
}

.a, a {
color: #9043cc;
}

.a:hover, a:hover {
color: #be7fef;
}

.calloutMenu {
background: #181817;
color: #a2a0a0 !important;
}

.con:not(.bgDark), .conSecond:not(.bgDark), #personCard .widgtContnt {
background: #181817;
}

.pageHeader.bgColor1 {
background: rgba(0, 0, 0, 0.8)!important;
}

.pageHeader.bgColor1~.pagePadded, .pageHeader.bgColor1~.pagePadded .treeViewerAwesome .openedNav .tNavWrap, .pageHeader.bgColor1~.pagePadded .treeViewerAwesome .breadCrumbs .breadCrumbList {
background: #000000;
}

.treeViewerAwesome .nodePhoto.iconFemale, .iconFemale, .userCardSize3 .userCardImg.iconFemale::before, .photo.iconFemale::before, .userCardImg.iconFemale {
background: #e91e63;
color: #f79dbb;
}

.treeViewerAwesome .nodePhoto.iconMale, .iconMale, .userCardSize3 .userCardImg.iconMale::before, .photo.iconMale::before, .userCardImg.iconMale {
background: #1e7ee9;
color: #9dc7f7;
}

.treeViewerAwesome .openedNav .female.thumbNode, .treeViewerAwesome .line.marriage-female {
background: #e91e63;
}

.treeViewerAwesome .openedNav .male.thumbNode,.treeViewerAwesome .line.marriage-male {
background: #1e7ee9;
}

.treeViewerAwesome .nodeContent .nodeInfo {
color: #585757;
}

html, body {
background: #000000;
color: #a2a0a0;
}

}
