css:
@-moz-document domain("angular.io") {
	.docs-content h2 {
		font-weight: bold;
	}
	p {
		font-size: 18px !important;
		line-height: 28px !important;
		font-family: arial !important;
		color: #111 !important;
		opacity: 1;
		text-align: justify !important;
	}
	table th,
	.text-subhead,
	.text-body,
	.docs-content p,
	.docs-content ul,
	.docs-content ol {
		opacity: 1;
	}
.sidenav-links .sidenav-section li a {
	color: #222;
	font-size: 14px;
	font-weight: 500;
}
.sidenav-links .sidenav-section>a {
	color: #222;
	font-size: 14px;
	font-weight: 500;
}
	[class*="code"] {
		font-size: 18px !important;
		line-height: 28px !important;
		font-family: "ubuntu mono" !important;
		padding: 2px 4px;
		border-radius: 0px;
	}
	code {
		font-size: 18px !important;
		line-height: 28px !important;
		font-family: "ubuntu mono" !important;
		padding: 2px 4px;
		border-radius: 0px;
	}
	p code {
		font-size: 18px !important;
		line-height: 28px !important;
		font-family: "ubuntu mono" !important;
		color: #00695C !important;
		border: none !important;
		background: rgba(236,239,241,0.56) !important;
		padding: 2px 4px;
		border-radius: 0px;
	}
	.side-nav .side-nav-secondary .nav-blocks a {
		color: #333;
	}

	.prettyprint .pln {
		color: #222;
	}
	.prettyprint .str,
	.prettyprint .atv {
		color: green;
	}
	.prettyprint .typ,
	.prettyprint .tag {
		color: #b12653;
	}
	.prettyprint .pun {
		color: #333;
	}
	table tbody th,
	table tbody td {
		color: #222;
	}
}
