css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("yahoo.com") {
  #YDC-Side, #YDC-UH, #YDC-Bottom, #YDC-Col2, #YDC-Stream-Proxy, #weather-map {
    display: none !important;
  }
  .MainCanvas, #YDC-Col1-Stack {
    margin: 0px !important;
  }
   
  #Col1-0-Weather > div > div:first-child {
    float: right !important;
  }
  
  #Col1-0-Weather > div > div:nth-child(2) {
    float: right !important;
    position: absolute;
    bottom: 0;
  }
  
  #YDC-Col1-Stack {
    position: absolute;
    width: 100%;
    top: 0;
    left: 0;
  }
  
  #Lead-1-WeatherLocationAndTemperature > div:nth-child(1) {
    height: 450px;
  }
  
  .Lts\(1px\) {
    font-size: 40px !important;
  }  
  #Lead-1-WeatherLocationAndTemperature {
    height: 800px !important;
  }

}
