css:
@-moz-document regexp("(https|http)?:\\/\\/boards(.+)\\.leagueoflegends\\.com/.*") {
/*.column.side {
    background-color: initial !important;
}*/
.backdrop {
    display: none;
}
#footer {
    background-image: url("https://cdn.leagueoflegends.com/apollo/assets/blue_magic.jpg")
}
#footer, body, tr, .side, .box, .reply.form-container textarea, .edit.form-container textarea, input, textarea, .dropdown a.btn span, .dropdown-menu, .glove, #my-updates .update-item, #my-updates .update-item .parent-comment, #comments .flat-comments .comment-list-item {
    background-color: #fff !important;
}
.new, body .byline, .discussion-title a, .apollo-header .sorting, #navigation-container .toggle, #navigation-container a:not(:hover), #board-description-sidebar .description-title, #board-description-sidebar .description:not(a), #discussion #content, .body, h1, h2, h3, h4, h5:not(.summoner-name), .discuss, .sorting label, .sorting .active, .reply.form-container textarea, .edit.form-container textarea, .preview .live-preview, .realm, .dropdown-menu a, .search-results tr.row .title .markdown-content {
    color: #000 !important
}
#riotbar-locale-switch-trigger {
    text-shadow: none;
}
.report-widget {
    background-color: #000;
}
.discussion-title a:visited, #board-description-sidebar a {
    color: #666 !important
}
.total-votes, .number, .sorting a {
    color: #6c543d !important
}
body a, body a:visited, #navigation-container a {
    color: #4a3926
}
.new-discussion-box a span {
    color: #94724D;
}
.inline-profile.isRioter a .username {
    color: #ae250f !IMPORTANT;
}
.report-widget a {
    color: #fff;
}
p a {
    color: #94724D
}
}
