css:
@-moz-document domain("opt.brain.com.ua"), url-prefix("http://opt.brain.com.ua/")
{

/*
// ==UserScript==
// @name Brain
// @namespace https://userstyles.org/users/273791
// @description Расширяет обзор сайта для оптовых покупателей.
// @author NewsIT
// @version 0.1
// @match opt.brain.com.ua
// @grant none
// ==/UserScript==
*/

  .thumb
  {
    max-height: 100px;
    max-width: 100px;
    padding: 5px;
  }

  .list *
  {
    font-size: 13px;
    line-height: 13px;
  }

  .list a
  {
    color: #035E06;
    font-family: calibri;
    font-size: 14px;
    font-weight: bold;
  }

  .list th
  {
    background: rgba(86,195,74,0.42);
    color: #F44336;
    font-size: 12px;
    padding: 2px;
    text-align: center;
  }

  .list .block_brief
  {
    color: #042C4C;
    font-family: Arial;
    font-size: 14px;
    font-weight: 400;
    margin-top: 10px;
  }
}
