css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.cinemay.com") {
  body {
    background: #444;
  }
  
  #page, .table tr:hover td {
    background: #666;
  }
  
  .newtitle h1, .unfilm, .sidebar_glob, .module-actionbar, .table thead tr th > div, .css-panes_series div, .table th {
    background: #888 !important;
    border-radius: 15px;
  }
  
  .searchSubmit {
    background: #888 url(data:image/png;base64,) no-repeat 6px 50% !important;
    color: #444;
  }
  
  #wp-members {
    background: #aaa !important;
    border-radius: 20px;
  }
  
  .module-actionbar .module-actionbar-shadow {
    background: none;
  }
  
  .sidebar_glob {
    border-radius: 0px 0px 0px 20px;
  }
  
  .newtitle h1, h2 {
    color: #ccc !important;
    border: #aaa 2px solid;
  }
  
  .imgpic img {
    border: 4px solid #888;
    border-radius: 20px;
  }
  
  .bordred small em, .module-actionbar tr th div, .summary div, .favorite, .pinterest a, table, .user-icn em, .twitter a, .facebook a, .google a, .share-links {
    color: #ccc !important;
  }
  
  .cat-item {
    color: #444 !important;
  }
}
