css:
@-moz-document domain("ngs24.ru") {
.adv,
.no-print,
.nn-right-side,
.nn-vote-box,
.ngs-informer,
.nn-footer,
.nn-more-box,
.article-informer-bar,
.ngs-sm-share-button,
.extended_comment-action_abuse,
.nn-other-arts-box,
.comment_form_add,
.ribbon-news-icons,
/*.subscribe-popup, .subscribe-popup-cover, .js-subscribe-popup-cover,*/
#ads-fix,
div[id^="div-gpt-ad"],
body > div.nn-wrapper > div.nn-content-wrap > div.nn-main-wrap > div > div.nn-main-table-open-tag > div:nth-child(2),
.nn-main-column>:not(.nn-articles-main):not(.nn-article-content):not(.page-comments)
{
    display:none!important;
}
.nn-main-column {
    margin: 0 0px 0 200px;
}
.nn-wrapper {
    max-width: 100%;
}
.nn-article-content {
    max-width: 100%;
}
.nn-articles-main__item {
    width: 100%;
}
    
}

@-moz-document url-prefix("http://ngs24.ru/news/articles") {
.nn-article-content {
    max-width: 100%;
}
}
