css:
    @-moz-document domain("wattpad.com") {
body {
        background-color: #f4edee !important;
    }
    #search-query {
        background-color: #f4edee !important;
        color:#654c4c !important;
        border:1px solid #654c4c !important;
        font-size:12px !important;
        font-family:tnr !important;
        letter-spacing:1px !important;
    }
    #content_wrapper {
        background-color: #f4edee !important;
    }
    #edit-controls {
        background-color: #f4edee !important;
        border: 2px solid #ffffff !important;
    }
    main {
        background-color: #ffffff !important;
    }
    .container {
        background-color: #f4edee !important;
        color: #654c4c !important;
        font-size:12px !important;
        font-family:tnr !important;
        letter-spacing:2px !important;
        border:1px solid #654c4c !important;
    }
    #creation-works-new {
        background: transparent !important;
        background-color: #ffffff !important;
    }
    .on-nav-item {
        color:#c69f79 !important;
        text-transform:lowercase !important;
        letter-spacing:2px !important;
        font-size:12px !important;
        
    }
    .container.left {
        background-color: #f4edee !important;
        color: #654c4c !important;
        font-size:12px !important;
        font-family:tnr !important;
        border:1px solid #654c4c !important;
        border-radius:3px !important;
    }
    .container.left > div > div {
       background-color: #f4edee !important;
        color: #654c4c !important;
        font-family:tnr !important;
    }
    .container_head {
        border:none !important;
        color: #654c4c !important;
        font-size:12px !important;
        font-family:tnr !important;
        letter-spacing:2px !important;
    }
    .commentby {
        background: transparent !important;
        float:left !important;
    }
    .commentbubble {
        padding: 3px !important;
        border: 1px solid #654c4c !important;
        background-color: #f4edee !important;
        color: #654c4c !important;
        font-family:tnr !important;
        font-size:12px !important;
    }
    .commentbubble>textarea {
        background-color: #f4edee !important;
        border: 1px solid #654c4c !important;
        border-radius: 1px !important;
        color: #A9A9A9 !important;
    }
    #commentbutton {
        border-radius: 10px !important;
        border: 1px solid #654c4c !important;
    }
    .commentbubble reverse {
        background-color: #f4edee !important;
    }
    .container.left>div {
        border: none !important;
    }
    .commentbubble.reverse {
        padding-left: 5px !important;
        padding-top: 3px !important;
        border:1px solid #654c4c !important;
        color: #A9A9A9 !important;
        font-family:tnr !important;
    }
    .commentauthor {
        border:1px solid #654c4c
    }
    #footer-container {
        background-color: #f4edee !important;
        border:1px solid #654c4c
    }
    #app-container {
        background-color: #f4edee !important;
    }
    .collection {
        background-color: #f4edee !important;
    }
    .module-title {
        color: #654c4c !important;
        background-color: #f4edee
    }
    .title {
        color: #654c4c !important;
    }
    .dropdown-menu {
        background-color: #f4edee !important;
        border: 1px solid #654c4c !important;
        border-radius: 4px !important;
    }
    .dropdown-menu > ul > li > a {
        color: #654c4c !important;
        transition: 0.5s !important;
    }
    .dropdown-menu > ul > li > a:hover {
        color: #654c4c !important;
        transition: 0.5s !important;
    }
    .dropdown-menu > div > ul > li > a {
        color: #654c4c !important;
        transition: 0.5s !important;
    }
    .dropdown-menu > div > ul > li > a:hover {
        color: #654c4c !important;
        transition: 0.5s !important;
    }
    .bold > li > a {
        color: #654c4c !important;
        transition: 0.5s !important;
    }
    .bold > li > a:hover {
        color: #654c4c !important;
        transition: 0.5s !important;
    }
    .panel {
        background-color: #f4edee !important;
        border: 2px solid #654c4c !important;
        border-radius: 1px !important;
        padding-left: 5px !important;
    }
    button.active {
        border-bottom: 1px solid #654c4c !important;
    }
    .on-switch-type {
        color: #654c4c !important;
    }
    #publish-count-wrapper {
        color: #a0a0a0 !important;
    }
    .story-description {
        background-color: #f4edee !important;
        color: #654c4c !important;
        border: 1px solid #654c4c !important;
        font-size:12px !important;
        padding:3px !important;
    }
    .category > label {
        color: #a0a0a0 !important;
    }
    .language > label {
        color: #a0a0a0 !important;
    }
    .copyright > label {
        color: #a0a0a0 !important;
    }
    .human-rating > label {
        color: #a0a0a0 !important;
    }
    select {
        background-color: #f4edee !important;
        color: #654c4c !important;
        border: 1px solid #654c4c !important;
    }
    .rating-message {
        color: #FFD700 !important;
    }
    #writer-navigation {
        background-color: #f4edee !important;
        border-bottom: 1px solid #654c4c !important;
        border-radius: 1px !important;
        font-size:12px !important;
    }
    .info {
        color: #a0a0a0 !important;
    }
    .background-lg {
        background-color: #f4edee !important;
        border-bottom: 1px solid #654c4c !important;
        border-radius: 1px !important;
    }
    #writer-editor {
        background-color: #f4edee !important;
        font-family:tnr !important;
    }
    #story-title {
        color: #654c4c !important;
        font-family:tnr !important;
    }
    .story-title > .on-navigate {
        color: #654c4c !important;
        transition: 0.5s !important;
    }
    .story-editor {
        background-color: #f4edee !important;
        color: #654c4c !important;
        border: 1px solid #654c4c !important;
        font-family:tnr !important;
    }
    #writer-editor-container {
        background-color: #f4edee !important;
    }
    #funbar {
        background-color: #f4edee !important;
    }
    .story-part {
        background-color: #f4edee !important;
    }
    .new-post {
        background-color: #f4edee !important;
    }
    .message > textarea {
        background-color: #f4edee !important;
        color: #654c4c !important;
        font-family:tnr !important;
    }
    .message > .triangle {
        display: none !important;
    }
    .panel-reading > pre > p {
        color: #654c4c !important;
        font-family:tnr !important;
        font-size:12px !important;
        text-align:center !important;
        text-transform:lowercase !important;
    }
    .panel-reading > h2 {
        color: #654c4c !important;
        font-family:tnr !important;
        font-size:19px !important;
        text-transform:uppercase !important;
    }
    .discover-rows {
        background-color: #f4edee !important;
         color: #654c4c !important;
    }
    .modal-body {
        background-color: #f4edee !important;
        border: 0px solid #ff9400 !important;
        border-radius: 3px !important;
    }
    .content {
        background-color: #f4edee !important;
    }
    .modal-title {
        color: #654c4c !important;
        transition: 0.5s !important;
    }
    .modal-title:hover {
        color: #654c4c !important;
        transition: 0.5s !important;
    }
    .description {
        color: #654c4c !important;
    }
    .description > pre {
        color: #654c4c !important;
        font-family:tnr !important;
        font-size:12px !important;
    }
    #progressbar {
        background-color: #000000 !important;
        background-image:url(http://www.easyfreepatterns.com/patterns/98/damask-brown-pattern-illustration-vector-to-download-at-featurepics-98207.jpg)  !important;
    }
    td {
        color: #654c4c !important;
    }
    .inboxmeta {
        color: #654c4c !important;
    }
    .sub-navigation {
        background-color: #f4edee !important;
    }
    .panel-footer {
        background-color: #f4edee !important;
        font-family:tnr !important;
    }
    .panel-heading {
        background-color: #f4edee !important;
    }
    .panel-title > .on-navigate {
        color: #654c4c !important;
        transition: 0.5s !important;
    }
    .panel-title > .on-navigate:hover {
        color: #654c4c !important;
        transition: 0.5s !important;
    }
    .fa {
        display: none !important;
    }
    .alt-link {
        color: #654c4c !important;
    }
    .pinned-item > .username {
        color: #654c4c !important;
        transition: 0.5s !important;
    }
    .pinned-item > .username:hover {
        color: #654c4c !important;
        transition: 0.5s !important;
    }
    .pinned-item > .body {
        color: #654c4c !important;
    }
    .story.small-width > strong > .on-navigate {
        color: #654c4c !important;
        transition: 0.5s !important;
    }
    .story.small-width > strong > .on-navigate:hover {
        color: #654c4c !important;
        transition: 0.5s !important;
    }
    #de {
        color: #654c4c !important;}}
