css:
@-moz-document url-prefix("http://www.spanishdict.com/") {
/**
 * SpanishDict Sublime Theme (Jun 21, 2016)
 * License: https://creativecommons.org/licenses/by-sa/4.0/
 */

 /* Hide elements */
 .sidebar-container, .adTop,
 .feedback-tab, .home-articles,
 .home-fluencia, .home-fluencia-variation2,
 #feedback-query, .adContent, .sd-ad-container,
 .fl-fourth-translation, .social-small {
 	display: none;
 }
 .ad-widget, .footer-wrapper {
 	display: none !important;
 }

 /* Stretch main container */
 .main-container {
 	width: 100%;
 }

 /* Site bg (heavy metal) */
 body, .menu-selector, .header-wrapper, .menu, .nav-content,
 .search-form-parts, .input-table-suggestion {
 	background: #272822;
 }
 
/* Lighter bg (kelp) */
.post-block.reference blockquote, .card,
#query, .nav-content {
	background: #36372f;
}

 /* Site text (athens gray) */
 span, h1, td, div, .vtable-label {
 	color: #F8F8F2;
 }
 
/* Hover bg (nepal) */
 .input-table-suggestion:hover,
 .input-table-suggestion.selected,
 .vtable tr:hover td {
 	background: #8eb1bc;
 }
.vtable tr:hover a.vtable-title-link {
	color: #8b4dff !important;
}
.icon-question-mark {
	display: none;
}

 /* Links (spray) */
 li > a, h3 > a, .heading2-anchor, .ques a, em > a, em ~ a, 
 small > a, .el > a, .search-history-items > a {
 	color: #66D9EF !important;
 }
/* active (st tropaz) ( */
li.last.active a {
	color: #295f8f !important;
}
.nav-content-item.nav-active .nav-content-item-text {
	border-bottom: 3px solid #66D9EF;
}

 /* (silver chalice) */
 .conj-basic-label {
 	color: #a5a5a5 !important;
 }

 /* Table labels (radical red) */
 .vtable-label {
 	color: #F92672 !important;
 }

 /* Table headings & examples (heliotrope) */
 .vtable-title-link {
 	color: #AE81FF !important;
 }

 /* Conj. subject (chenin) */
 .vtable-pronoun {
 	font-style: italic;
 	color: #E6DB74 !important;
 }

 /* Example highlited word (pear) */
 .megaexamples-highlight {
 	color: #A6E22E;
	background: none;
 }
 
/* Paigination number */
.megaexamples-page-active, .pagination > ul > li.active > a {
	color: #272822 !important;
}
}
