css:
@-moz-document domain("www.youtube.com") {
.watched span {
    opacity: 0.8;
    transition: opacity 0.3s ease;
}

.watched span:after {
    background: rgba(50,150,250,0.6);
    content: '';
    height: 100%;
    position: absolute;
    transition: opacity 0.3s ease;
    width: 100%;
    z-index: 100;
}

.watched:hover span {
    opacity: 1;
}

.watched:hover span:after {
    opacity: 0;
}
}
