css:
@-moz-document domain("real-debrid.com") {
#aside1_v1, #header {
 background:url('https://i.imgur.com/k4XgPOI.jpg');
}

#aside1_v1 {
    height: 150px;
    width: 100%;
    border-top: 0px;
    border-bottom: 1px solid #ddd;
    background-position: 0 center;
    background-size: 77%;
}
#header {
    background-size: 77%;
    background-position: 0 -70px;
}
.header_pattern {
    background: none;
}
#header > div > div {
    background: #2b2b2b;
    height: 80px;
    border-radius: 0 0 25px 25px;
    border: 1px solid white;
    border-top: none;
}
#logo {
    margin-left: 10px;
}

.downloadSelectedCheckbox {transform:scale(2);}

#header_img_right {position: relative;top: 20px; margin: 0 0 0 500px;}
}
