css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("discordapp.com") {
body, .comment-published, .article-vote-count {
    color: #000;
}
    
.comment-published, .article-vote-count, .meta, .meta-group {
    color: #7088D8;
    font-weight: bold;
}
    
.side-column li a {
    color: #0390C5;
    font-size: 18px;
}
    
*::-moz-placeholder {
    color: #000;
}
    
.wysiwyg-color-yellow110, .wysiwyg-color-green80, .wysiwyg-color-cyan110, .wysiwyg-color-orange80 {
    text-shadow: 0px 0px #000;
    font-weight: bold;
    color: #EDD4000;
}
.wysiwyg-color-black {
    text-shadow: none;
}
}
