css:
@-moz-document url-prefix("https://www.zhihu.com/") {
body {
    background-color: #1c1f21;
    color: #c4c5b4;
    
}
b {
    color:#FFF;
}
.zu-top {
    background: rgba(0, 0, 0, 0.8);
}
.side-topic-avatar {
    display: none;
}
.shameimaru-section {
    display: none;
}
#zh-top-link-logo {
    display: none;
}
.feed-item .avatar img {
    border-radius:100%;
}
a {
    color:#1a8812;
}
.feed-item+.feed-item, .feed-separator+.feed-item, .zm-side-nav-group+.zm-side-nav-group {
    border-top:1px solid #000;
}
.zu-top-search-input {
    background:#333;
    color: #ccc;
}
.zu-top-search-input:focus {
    background:#333;
    color: #ccc;
}
.zu-top-nav-live-inner {
    background:#222;
}
.zm-noti7-content-body .zm-noti7-content-item.unread {
    background:#444;
}
.zm-side-nav-link.active, html.no-touchevents .zm-side-nav-roundtable .follow-link:hover+.zm-side-nav-link, html.no-touchevents .zm-side-nav-link:hover {
  	background:#444;
    color:#499ce8;
}
.zm-item-vote-count {
   background:#333;
}
.zm-item-answer-author-info {
	color:#8da182;
}
body .highlight {
    background:#444;
}
.editable {
    background:#000;
}
.zm-editable-editor-field-wrap {
    background:#000;
}
.zm-comment-list {
    background:#000;
    color:#ccc;
}
.zm-item-comment .zm-comment-content {
     color:#ccc;
}
.zm-item-comment+.zm-item-comment {
    border:none;
}
.zm-comment-box.empty.cannot-comment .zm-comment-box-ft, .zm-comment-box .zm-comment-box-ft {
    background: #0C242B;
}
.zm-comment-form .zm-comment-textarea, .zm-comment-form .zm-comment-editable {
    background:#000;
    color:#eee;
}
.zm-comment-box .load-more {
    text-shadow:none;
}
.zm-comment-box:hover .load-more:hover {
    background:#444;
}

.side-topic-avatar {
    display:none;
}
._CommentItem_content_CYqW {
color:#ccc;
}
}
