css:
@-moz-document domain("konetix.pl") {
.h1, body  {
    background: #000 !important;
}
.footer, .comments, .main-body > i {
    display: none !important;
}

.panelsr, .panelle, .panelpr, .side-border-left, .side-body, .capmain, .panellewo {
    background: #212121 !important;
    color: #bababa !important;
}
.itemborderlegendarny, .itemborderheroiczny {
    background: none !important;
    border-radius: 0px !important;
    border-width: 2px !Important;
}
.side {
    background: #313131 !important;
    margin-top: 2px !important;
}
.tabelanex2, .tabelanex2 td, .tabelanex, .tablerow0, .tablerow1, .tablerow0 > td, .tablerow1 > td {
    background: #212121 !important;
    border: 1px solid #313131 !important;
}
.herotab {
    background: #212121 !important;
    border: 1px solid #313131 !important;
    color: #000 !important;
}
.tytultd {
    text-shadow: none !important;
    color: #bababa !important;
}
* {
    box-shadow: none !important;
    text-shadow: none !important;
}
strong, .tytan, .heros, a {
    color: #bababa !important;
}
font {
    color: #939393 !important;
}
strong > div, .tablerow0 > td > center > font > div, .tablerow1 > td > center > font > div {
    background: url('http://i.imgur.com/TVjtl25.png') !important;
        }
strong > div > img {
    background: url('http://i.imgur.com/2hDOSsF.png') !important;
}
}
