css:
@-moz-document url-prefix("http://forum.mc-skyblock.fr") {
/*Code crée par Théo aka _Skyrock.
Merci de ne pas le modifier ! */

#clearBackground {
background-image : url("http://i.imgur.com/plzvAP6.jpg");
}

.navTabs .navTab.selected, #navigation .pageContent, body #moderatorBar, .titleBar {
background : url("http://media-elerium.cursecdn.com/attachments/23/535/2015-06-18_14.png") 100% 0 fixed;
}

.navTabs { 
background : none;
}

.navTabs .navTab.selected .tabLinks {
background : rgba(255,255,255,0.1);
}

.titleBar {
box-shadow : inset rgba(255,255,255,0.2) 0px 1px 0px, inset rgba(255,255,255,0.1) 0px 100px 0px
}

.navTabs .navTab.selected a.navLink, .navTabs ul.visitorTabs > li.navTab.selected a.navLink {
background : rgba(255,255,255,0.1)
}

.footer .pageContent {
background : rgba(255,255,255,0.1)
}

#QuickSearch:not(.active) #QuickSearchQuery {
background: rgb(35, 35, 35);
border-color: rgb(83, 83, 83);    
}

a.callToAction span {
background-color: rgb(175, 175, 175)
}

.nodeIcon:before {
color: rgb(218, 218, 218);
}

body > footer {
visibility: hidden;
}

#content > div > div > aside > div > div.section.infoBlock.sharePage {
display: none;
}
}
