css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("golibgen.io") {

* { color: #b1e10a !important; background: 



#0D0003 !important; border-color: #333 !important; text-shadow: none !important; }



a, a * { color: #b1e10a !important; text-decoration: none !important; }



a:visited, a:visited * { color: #b1e10a !important; }



a:hover, a:hover * { color: #f00 !important; }



img.wp-smiley,



img.emoji {



	display: inline !important;



	border: none !important;



	box-shadow: none !important;



	height: 1em !important;



	width: 1em !important;



	margin: 0 .07em !important;



	vertical-align: -0.1em !important;



	background: none !important;



	padding: 0 !important;



}







.broken_link, a.broken_link {



	text-decoration: line-through;



}







/* Style generated by theme (global compiled style) */







:root .adsbygoogle



{display:none !important;}







/* custom css theme panel */



.links-w a{background: #b1e10a; color: #0D0003; font-weight: bold; font-size: 16px; padding: 10px; line-height: 50px;}



.links-w a:hover{background: #2b2b2b; color: #0D0003;}



.raiola {width: 75%; height: auto; padding: 7px; margin-bottom: 7px; font-size: 30px; background: #2DADE0; color: #b1e10a; text-shadow: 1px 1px 1px #b1e10a; text-align: center;}



.raiola a {color: #0D0003;}


}
