css:
@-moz-document url-prefix("http://www.boost.org/doc/libs/") {
div.section
{
    max-width: 60em;
    margin: 0 auto 0 auto;
}

div.section p,
div.section li,
div.section td > *
{
    max-width: 40em;
}

/* Keep code inside the central column. */
pre {
    white-space: pre-wrap;       /* Since CSS 2.1 */
    white-space: -moz-pre-wrap;  /* Mozilla, since 1999 */
    white-space: -pre-wrap;      /* Opera 4-6 */
    white-space: -o-pre-wrap;    /* Opera 7 */
    word-wrap: break-word;       /* Internet Explorer 5.5+ */
}























.copyright-footer:after
{
    text-align: right;
    content: "Get Vulkan examples and helper code at krust.org.";
}
}
