css:
@-moz-document domain("www.theblaze.com") {
* {
    background-color: transparent !important;
    border-color: #ff0000 !important;
    color: #ffffff !important; 
    font-family: Georgia,Times New Roman,Times,serif;
    font-weight: bold !important;
}

html {
    background:url(http://i.imgur.com/wwP3YWT.jpg) no-repeat center fixed !important;
    background-size:cover;-o-background-size:cover;-webkit-background-size:cover !important;
}

.video-widget .widget__body, .slider_content {
    margin: 0 auto;
    padding: 40px;
    background-color: #000000bd !important;
}

.container {
    background-color: #000000bd !important;
}

.headline-with-background .widget__headline, .headline-with-background .widget__headline:hover, .featured-stories .posts-container--first .widget__headline, .featured-stories .posts-container--first .widget__headline:hover, .post-page .post-partial .widget__body .headline-container {
    box-shadow: -10px 0 0 rgba(234, 234, 234, 0), 10px 0 0 rgba(234, 234, 234, 0);
    -moz-box-shadow: none;
    transition: all 0.2s ease;
}
}
