css:
@-moz-document domain(readcomics.tv) {
body {
    height: 100%;
    margin: 0;
    padding: 0;
}
.header,
.main div:nth-of-type(5),
.footer,
.ct-left,
.imgresizediv,
.title,
.label, 
.top_page_select_col {
    display: none;
}
.container {
    position:absolute;
    margin: 0;
    float: none;
}
.full-table tr td {
    padding:0;
}
.chapter-title .ct-right a.nav {
    height:100vh;
    width: 50%;
    box-sizing: border-box;
    margin-left: 0;
    background-color: rgba(0,0,0,0);
    color: rgba(0,0,0,0);
}
.chapter-title .ct-right a.nav:hover {
    background-color: rgba(0,0,0,0);
}
.chapter-title .ct-right a.nav.prev {
    float: left;
}
.chapter-title .ct-right a.nav.next:before,
.chapter-title .ct-right a.nav.prev:before {
    border: 0;
}
.chapter-title .ct-right {
    position: absolute;
    top: 0;
    left: 0;
	z-index: 1000;
    width:100%;
    max-width: none;
    float: none;
    margin: 0;
    padding: 0;
}
#main_img {
    display: block;
    max-width: none !important;
    height: 100vh;
    margin: 0 auto;
    position: relative;
    z-index: 1;
}
}
