css:
/*
    file:
        poster on top left.css
    
    function:
        put poster info on top left corner of thread message box
    
    version:
        v0.10
            - draft
*/
@-moz-document
    domain(communities.apple.com),
    domain(discussions.apple.com),
    domain(discussionsjapan.apple.com),
    domain(discussionskorea.apple.com),
    domain(discussionschinese.apple.com)
{
    /* ------------------------------------------------------------------------------------------------
        THREAD CONTENT BROWSER (ADDITION)
        - poster on top left
    */
    .j-thread-post footer .footer-profile {
        position: absolute !important;
        top:  -12px !important;
        left: -35px !important;
    }
    .j-inline-correct-answer footer .footer-profile {
        position: absolute !important;
        top:   3px !important;
        left: -15px !important;
    }
    .j-inline-promoted-helpful-answer footer .footer-profile,
    .j-inline-recommended-answer footer .footer-profile {
        position: absolute !important;
        top:   3px !important;
        left: -5px !important;
    }
    .j-thread .jive-content .j-thread-post > header,
    .j-thread .jive-content .j-thread-post > h3.header {
        padding: 35px 0px 0px !important;
    }
    .j-inline-promoted-helpful-answer > header,
    .j-inline-promoted-helpful-answer > h3.header,
    .j-inline-recommended-answer > header,
    .j-inline-recommended-answer > h3.header {
        padding: 35px 0px 0px !important;
    }
    .j-inline-correct-answer > header,
    .j-inline-correct-answer > h3.header {
        padding: 45px 0px 0px !important;
    }
}
