css:
@-moz-document domain("besports.be") {
.header {
    color: #4d4d4d !important;
    background-color: #4d4d4d;
}
i.icon {
	color: white;
}
footer {
    background-color: #4d4d4d;
}
#homepage-games {
    background-color: #4d4d4d !important;
}
.body-wrapper {
    min-height: 100%;
    background-image: url("http://i.imgur.com/Rw6OBxz.jpg") !important;
    position: relative;
    transition: all 0.4s;
}
.games-header.games-title {
    font-size: 300%;
    color: #b3b3b3;
}
.games-header {
    font-family: 'Dosis', sans-serif;
    font-weight: 500;
    color: #b3b3b3;
}
.tournament .tournament-information .tournament-main{
    padding: 10px 30px;
    background-color: #4d4d4d;
    background-image: url("http://i.imgur.com/Rw6OBxz.jpg");
    color: #b3b3b3;
}
.tournament .tournament-information .tournament-side .block-information .tournament-side-content-wrapper {
    display: block;
    background-color: #4d4d4d;
    font-weight: bold;
    background-image: url("http://i.imgur.com/Rw6OBxz.jpg");
    color: #b3b3b3;
}
.tournament .tournament-information .tournament-side .block-participants .tournament-side-content-wrapper {
    display: block;
    background-color: #4d4d4d;
	font-weight: bold;    
    background-image: url("http://i.imgur.com/Rw6OBxz.jpg");
    color: #b3b3b3;
}
.tournament .tournament-information .tournament-side .block-prizepool .tournament-side-content-wrapper {
    display: block;
    background-color: #4d4d4d;
    font-weight: bold;
    background-image: url("http://i.imgur.com/Rw6OBxz.jpg");
    color: #b3b3b3;
}
.information-element a {
    color: #fff;
    font-weight: bold;
}
.bracket-innerwrapper {
    position: relative;
    transition: all 1s;  
    width: 100%;
    height: 100%;
}
.bracket-box {
    background-image: url("http://i.imgur.com/Rw6OBxz.jpg");  
}
element.style {
    background-image: url("http://i.imgur.com/Rw6OBxz.jpg") !important;
    background-attachment: scroll;
    background-size: auto;
    background-position: center top;
}
.ui.vertical.menu {
	background-image: url("http://i.imgur.com/Rw6OBxz.jpg") !important;
    color: #b3b3b3;
}
.ui.menu .header.item, .ui.vertical.menu .header.item {
    margin: 0em;
    text-transform: none;
    color: #000000 !important;
    font-weight: bold;
}
.ui.menu .item {
    color: #000000;
}
.ui.segment {
    color: #000000;
    background-color: #b3b3b3;
}
.ui.cards>.card>.content, .ui.card>.content {
    color: #000000;
    background-color: #b3b3b3;
}
.overview-page .overview-main .featured-full {
    padding: 2%;
    background: #b3b3b3;
    margin-bottom: 3%;
}
.ui.block.header {
    background: #b3b3b3;
}
.overview-page .overview-sidebar .sidebar-logo, .overview-page .overview-sidebar .sidebar-description {
    background: #fff;
}
.ui.inverted.blue.segment {
    background-color: #4d4d4d !important;
    color: #ffffff !important;
}
.ui.blue.labels .label, .ui.blue.label {
    background-color: #4d4d4d !important;
    border-color: #4d4d4d !important;
    color: #ffffff !important;
}
.featured-tournaments .featured-fulls .full-details .button-wrapper {
    float: right;
    padding: 8px 12px;
    background-color: #4d4d4d !important;
}
.featured-tournaments .featured-fulls .full-details .details-info .details-info-label {
    font-weight: 300;
    color: #000000 !important;
    font-size: 200%;
}
.featured-tournaments .featured-fulls .full-details .details-link a {
    color: #000000;
    text-decoration: none;
}
.ui.blue.buttons .button, .ui.blue.button {
    background-color: #4d4d4d !important;
    color: #ffffff;
}
.featured-tournaments .featured-fulls .full-image {
    width: 60%;
    float: left;
    opacity: 0.5;
}
.featured-tournaments .featured-thumbnails .thumbnails-picture {
    opacity: 0.5 !important;
}
}
