css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("4chan.org") {

body {
    background: #fcfcf7;
    margin: 0;
    padding: 0;
}

div.thread {
    max-width: 900px;
    margin: 0 auto;
}

/* header */
#boardNavDesktop {
    background: white;
    border-bottom: 1px solid #e5e3db;
    padding: 2px 5px;
}
#boardNavDesktop a, #boardNavDesktop span {
    font-size: 12px;
    color: black !important;
}
/* post */
.post.reply {
    background-color: white;
    border: 1px solid #eee;
    border-bottom-width: 0;
    margin: 0;
    width: 90%;
}
.reply.highlight {
    background-color: #f2f1e9 !important;
    border: 1px solid #f2f1e9 !important;
    border-bottom: 0px solid red !important;
}
.replyContainer {
    clear: left;
}
.postMessage {
    font-size: 16px;
}
.sideArrows {
    display: none;
}

/* quote */
.preview {
    background-color: #f2f1e9 !important;
    border: 1px solid #ddd !important;
    border-bottom-width: 0;
    margin: 0;
}

#quote-preview {
    max-width: 500px;
}

/* border */
hr {
	border-top: 5px solid #e5e3db;
}

}
