@-moz-document domain("livebook.com.ua") {
@media (max-width: 1000px) {
	.content_left {
		left: 0;
		z-index: 20;
		transition: all 0.5s;
		background: none !important;
		display: block;
		position: absolute;
		padding-top: 35px;
		transform: translateX(-95%);
	}
	.content_left:hover {
		height: 100%;
		max-height: 100%;
		max-width: 100%;
		box-shadow: 0 0 15px rgba(0, 0, 0, .3);
		transform: translateX(0px);
	}
	.content_block {
		height: 100%;
		min-height: 100%;
		max-width: 100%;
		padding-bottom: 100%;
		box-shadow: 0 0 15px rgba(0, 0, 0, .3);
	}
	.scroll_fix_bg {
		/*top: 530px;
width: 50px;
padding: 5px; */
		
		display: none !important;
	}
	.content_left .content_block li {
		transform: translateX(-240px);
		transform-style: preserve-3d;
		transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
		transition-delay: 0.1s;
		transition-duration: 0.3s;
	}
	.content_left .content_block {
		transform: translateX(-240px);
		transform-style: preserve-3d;
		transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
		transition-delay: 0.1s;
		transition-duration: 0.3s;
	}
	.content_left:hover .content_block li {
		transform: translateX(0px);
	}
	.content_left:hover .content_block {
		transform: translateX(0px);
	}
	[src*="logua.com/img.php"] {
		display: none;
	}
}
