css:
@-moz-document domain("wikia.com") {
/* Osman Vielma (2016) */

div.mw-content-ltr {
    font-size: 20px !important;
    text-align: justify !important;
    font-family: SF UI Display, Helvetica !important;
}

.pi-theme-frwiki .pi-data .pi-secondary-font, .pi-theme-rlc .pi-data .pi-secondary-font, .pi-theme-plant .pi-data .pi-secondary-font, .pi-theme-fungus .pi-data .pi-secondary-font, .pi-theme-road .pi-data .pi-secondary-font, .pi-theme-mountain .pi-data .pi-secondary-font, .pi-theme-at-will .pi-data .pi-secondary-font, .pi-theme-daily .pi-data .pi-secondary-font, .pi-theme-encounter .pi-data .pi-secondary-font, .pi-theme-ritual .pi-data .pi-secondary-font, .pi-theme-aberration .pi-data .pi-secondary-font, .pi-theme-elemental .pi-data .pi-secondary-font, .pi-theme-fey .pi-data .pi-secondary-font, .pi-theme-immortal .pi-data .pi-secondary-font, .pi-theme-shadow .pi-data .pi-secondary-font, .pi-theme-ship .pi-data .pi-secondary-font {
    font-family: Helvetica !important;
    font-weight: bolder;
    font-size: 13px;
    color: #000000 !important;

}

.pi-theme-frwiki .pi-font, .pi-theme-rlc .pi-font, .pi-theme-plant .pi-font, .pi-theme-fungus .pi-font, .pi-theme-road .pi-font, .pi-theme-mountain .pi-font, .pi-theme-at-will .pi-font, .pi-theme-daily .pi-font, .pi-theme-encounter .pi-font, .pi-theme-ritual .pi-font, .pi-theme-aberration .pi-font, .pi-theme-elemental .pi-font, .pi-theme-fey .pi-font, .pi-theme-immortal .pi-font, .pi-theme-shadow .pi-font, .pi-theme-ship .pi-font {
    text-align: left;
    font-family: Helvetica !important;
    font-weight: normal;
    font-size: 14px;
}


div.WikiaPageBackground {
    background: #f9e2b6 !important;
}
}
