css:
@-moz-document url-prefix("http://www.leboncoin.fr"), url-prefix("https://www.leboncoin.fr") {
#header .headerContent {
    background: #3873bd;
}
.button-white-mobile i {
    color: #3873bd;
}
.button-white, .button-white-mobile {
    color: #3873bd;
}
.tabsSwitch:hover, .tabsSwitch.active, .tabsSwitch:focus {
    color: #3873bd;
}
.item_price {
    color: #3873bd;
}
.pagination_links .element.selected {
    background: #3873bd;
}
.item_imageNumber span {
    color: #3873bd;
}

@media (max-width: 971px)
{
.headerNav .logo-site {
    background-color: #3873bd;
}
.headerNav a:hover:not(.logo-site), .headerNav a:active:not(.logo-site), .headerNav a.active, .headerNav button:hover:not(.logo-site), .headerNav button:active:not(.logo-site), .headerNav button.active {
    color: #3873bd;
}
.headerNav a, .headerNav button {
    color: #777;
}

    .headerNav a:hover:not(.logo-site),
    .headerNav a:active:not(.logo-site),
    .headerNav a.active,
    .headerNav button:hover:not(.logo-site),
    .headerNav button:active:not(.logo-site),
    .headerNav button.active {
        color: #3873bd;
    }
}
.orange {
    color: #3873bd;
}
.carousel>i {
    color: #3873bd;
}
.carousel .thumbnails ul li.thumb.selected {
    border-color: #3873bd;
}
.button-orange, .button-orange-mobile {
    background: #3873bd;
}
.box-orange>.title {
    background: #3873bd;
}
.box-orange {
    border: 2px solid #3873bd;
}
}
