css:
@-moz-document domain("twitch.tv") {
.app-main.theatre .ember-chat .chat-messages .chat-line .from {
  color: #369 !important;
}

.app-main.theatre .ember-chat .chat-messages .chat-line span.from[style="color:#D2691E"] {
  color: #D2691E !important;
}

.app-main.theatre .ember-chat .chat-messages .chat-line .message:not([style="undefined"]) {
  color: #69c !important;
}

.ember-chat .chat-messages .chat-line {
  font-size: 16px;
}

.app-main.theatre #main_col {
  margin-right: 0;
}

.app-main.theatre #right_col {
  opacity: .5;
}

.app-main.theatre #right_col:hover {
  opacity: 1;
}


.app-main.theatre .ember-chat .badges .moderator {
  background-color: transparent;
}
          
.app-main.theatre .ember-chat .badges .turbo {
  display: none;
}


.app-main.theatre .channel-stats .stat.live-count {
  position: absolute;
  z-index: 10;
  bottom: 0px;
  right: 246px;
  color: #999;
}

.app-main.theatre .channel-stats .stat.live-count svg path {
    fill: #666;
}

.app-main.theatre .player-buttons-right {
    margin-right: 340px;
}

}
