css:
@-moz-document url-prefix("https://docs.google.com/document/") {
#docs-editor {
    background-color: #000000 !important;
}
.kix-page {
    background-color: #131313 !important;
    border: 0px !important;
}
.kix-page-paginated {
    border-top: 1px solid #333333 !important;
}
.kix-paginateddocumentplugin {
    border: 0px !important;
}
.docs-ui-unprintable:last-child {
    background-color: #000000 !important;
}
.kix-cursor-caret {
    border-color: #FFFFFF !important;
}
.kix-page,
.kix-page * {
    color: #ffffff !important;
}
#docs-toolbar-wrapper,
#docs-bars,
#docs-chrome * {
    background-color: #131313 !important;
    color: white !important;
    border-color: #131313;
}
#docs-toolbar-wrapper {
    background-color: #131313;
    color: #4285F4;
    background-image: -webkit-linear-gradient(top, #131313, #131313);
    box-shadow: inset 0 1px 0 0 #131313;
}
#docs-titlebar-share-client-button,
#jfk-button,
#docs-docos-commentsbutton {
    margin-right: 0;
    background: black;
    border: black;
}
}
