@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("virustotal.com") {
  /* Background */
  body, .footer, .form-actions {
    background: #444242 !important;
  }
  .comment-text, .well, .dropdown-menu, #antivirus-results > tbody > tr:nth-child(2n+1) > td, #comment-signin {
    background: #303030 !important;    
  }  
  #dlg-signin, .alert, .modal-body, .modal-header, .modal-footer {
    background: #212121 !important;
  }
  
  /* white background to logos */
  #logo.center {
    padding-right: 16px !important;
  }
  img[alt="VirusTotal"] {
    background: #FFF !important;
    border-radius: 5px !important;
    padding: 5px 8px 0 8px !important;
    box-shadow: 0 0 10px #212121 !important;
  }

  /* Texts */
  a:hover, #message {
    color: #FFF !important;
  }
    a, strong, h3, h1, .action, .control-label, .header {
    color: #DDD !important;
  }
    td,  dt, address {
    color: #BBB !important;
  }
  .text-gray, span, .comment-text, p, dd, li {
    color: #AAA !important;
  }
  .important{
    color: #303030 !important;
  }
  .enum {
    color: #AAA;
  }  
  
  /* Tab lists */
  .navbar-inner {
    background: #303030 !important;
  }
  li, li > a {
    transition-duration: .25s !important;
  }
  li.active {
    background: #616161 !important;
  }
  li:hover {
    background: #0F75BC !important;
  } 
  li > a {
    color: #DDD !important;
    text-shadow: none !important;
    background: none !important;
  }  
  li > a:hover {
    color:  #FFF !important;
  }
  .dropdown-menu > li:hover, .dropdown-menu > li:hover {
    background: #444242 !important;
  }
  
  /* Buttons */
  .action, #btn-more-reputation, #btn-sign-in, .btn.cancel, .btn {
    background: #0F75BC !important;
    border-radius: 2px !important;
  }
  .action:hover, #btn-more-reputation:hover, #btn-sign-in:hover, .btn.cancel:hover, .btn:hover {
    background: #094670 !important;
  }
  .action {
    padding-right: 1px !important;
  }
  
  /* No boarders and hyperlinks underline */
  * {
    border: none !important;
    text-decoration: none !important;
    text-shadow: none !important;
  }  
}
