css:
@-moz-document domain("animebytes.tv") {
body{
    background: #222222 !important;
    color: grey !important;
}
#wrapper{
	background: #222222 !important;
}
#content{
	background: #222222 !important;
    color: grey !important;
}
#content h3{
    color: grey !important;
}
#content h2{
    color: grey !important;
}
#searchbars{
	background: #222222 !important;
}
#content  div{
	background: #222222 !important;
    color: grey !important;
    border-color: gray !important;
}
#content  dl{
	background: #222222 !important;
    color: white !important;
    border-color: gray !important;
}
#content  ul{
	background: #222222 !important;
    color: grey !important;
}
#content  tr{
	background: #222222 !important;
    color: grey !important;
}
#content  a{
    color: #0069c5 !important;
}
#content  span > a{
    color: white !important;
}
#content  tr > td{
    background: #222222 !important;
    color: gray !important;
}
#content tr blockquote{
    color: gray !important;
}
.page-link{
    border-color: gray !important;
}
.next-prev{
    border-color: gray !important;
}
#browse_search > form label{
    color: white !important;
}
#quickpost{
    background: #222222 !important;
}
#footer_inner > div > h3{
    color: gray !important;
}
#searchbars > ul{
    background: black !important;
    color: white !important;
}
#menu > ul > li{
    background: black !important;
    color: white !important;
}
#menu > ul > li > a{
    background: black !important;
    color: white !important;
}
#searchbars > ul > li > form input{
    border-color: gray !important;
    border-style: solid !important;
}
#categories{
    border-right: 3px solid gray;
}
#content > div.thin > h2{
    color: gray !important;
}
#content > div.thin > h3{
    color: gray !important;
}
#content select{
    background: gray !important;
    color: white !important;
}
#content input{
    background: gray !important;
    color: white !important;
}
#news > div > hr{
    background: gray !important;
    border-color: gray !important;
}
#nav_torrents > ul > li{
    background: black !important;
    color: white !important;
}
#nav_torrents > ul > li > a{
    color: white !important;
}
#nav_torrents > ul{
    border-color: gray !important;
}
#nav_siteinfo > ul > li{
    background: black !important;
    color: white !important;
}
#nav_siteinfo > ul > li > a{
    color: white !important;
}
#nav_siteinfo > ul{
    border-color: gray !important;
}
#user_comments > h2{
    color: gray !important;
}
#user_comments > h3{
    color: gray !important;
}
}
