css:
@-moz-document url-prefix("http://moetube.net/room/") {
#vidholder {
    height: calc(100% - 150px);
    width: 100%;
}
#maincontainer {
    width: 100%;
    margin-top: 60px;
    margin-bottom: 0px;
}
#rbox {
    transition: 0.3s;
    margin: 0px;
}
#lbox {
    width: calc(100% - 362px);
}
#anititle {
    margin-top: 0px;
}
#chatbox { 
    height: calc(100% - 185px);
}
}
