css:
@-moz-document url-prefix("https://jira.your.site") {
textarea#description {
  font-family: monospace; 
}

.ghx-issue { height: auto; }
.ghx-issue .ghx-grabber, .ghx-issue:first-child .ghx-grabber { height: 100%; }
.chrome .ghx-issue-fields .ghx-summary .ghx-inner { height: auto; }
.ghx-issue-fields .ghx-summary .ghx-inner { display: inline; }


.ghx-swimlane-header { font-size: 1.5em; font-weight: 700 }

.ghx-column[data-column-id="790"]{ width: 15%; opacity: .3 }
.ghx-columns .ghx-column.ghx-busted-max { background: transparent; }

.ghx-issue .ghx-issue-content {min-height: 0}
.ghx-issue-fields .ghx-key {display: none;}
.ghx-issue .ghx-extra-fields {margin-top: 0;}
.ghx-extra-fields .ghx-extra-field-row:nth-child(1)  {color: #4caf50; float: right; padding-right: 10px;}
.ghx-extra-fields .ghx-extra-field-row:nth-child(2)  {opacity: 0.5; display: inline;}
.ghx-extra-fields .ghx-extra-field-row:nth-child(3)  {display: none}
.ghx-issue .ghx-end, .ghx-issue .ghx-flags {display: none}

.ghx-issue.ghx-has-avatar .ghx-issue-fields {float: left; padding-right: 20px;}
.ghx-issue.ghx-has-avatar .ghx-avatar {display: none;}
.ghx-issue .ghx-issue-content {padding: 12px 10px 8px 38px;}
.ghx-issue .ghx-type {top: 13px;}
}
