css:
@namespace url(http://www.w3.org/1999/xhtml);


#PlacesToolbarItems > toolbarbutton > image{display: none !important;}

@-moz-document domain("2ch.net") {


  body {
    background: none;
  }
  
  .ad--right {
    display: none;
  }
  
  a {
    color: black;
    font-weight: 100;
    text-decoration: none;
  }

  h1.title {
    color: black;
  }
  
  
  div.topmenu a.menuitem, div.topmenu a.menuitem:visited {
      color: black;
    font-size: 0.5em;
  }
  
  #banner {
    display: none;
  }
  
  
  .number {
      font-size: 0.8em;
  }
  
  .name {
    font-size: 0.8em;
  }
  
  
h1.title::after {
    content: "- - - - - - - - - - テスト用ダミーテキスト表示開始- - - - - - - ";
    display: block;
    font-size: 5em;
}
  
/*  .post::before {
    content: "- - - - - - - - - テスト用ダミーテキスト表示開始　system debug - - - - - - - - - - - - - - - ";
    font-size: 2em;
    display: block;
  }
  */
  
  .post::after {
    content: "- - - - - - - - - 開発テスト用　system debug - - - - - - - - - - - - - - - ";
    font-size: 2em;
    display: block;
  }
  
  
  
  
.topmenu:before {
    content: '開発用デバッグ表示';
    display: block;
    font-size: 3em;
}
  
  
.stoplight,.stopyellow,.stopred {
    background-color: black;
  font-size: 0.5em;
}
  
  
h1.title {
    font-size: 0.5em;
}
  
  
.thread {
    margin: 0;
}
  
  
}
