css:
@-moz-document domain(campus.tum.de), domain(campusquality.tum.de), domain(campusdemo.tum.de) {
    body
    {
        font-size: 1em;  /* larger font size */
    }

    th.tblHdr, th.tblHdrC, th.tblHdrL, th.tblHdrR, /* LEH */
    table.cotable th /* LV, RES, MHB */
    {
        background-color: hsl(211,58%,85%) !important;
    }
    
    .hi 
    { 
        /* hintergrund für mouseover in tabellen, nur pseudo-class :hover benoetigt */ 
    }
    .hi:hover 
    {
        background-color: #fdfdd8 !important;
    }

    #wbOrg_site_resources div.wbOrg_site_resources_legend, /* Orgpage */
    #tabIdOrgModulestab_container legend.Mask, /* MHB */
    div.MaskBackground legend.Mask, legend.Mask, legend /* SPO */
    {
        border-bottom: solid 0.2em hsl(211,58%,55%);
    }

    #wbOrg_site_resources div.wbOrg_site_resources_legend td /* Orgpage */
    {
        text-align: center;
    }
    
    body.menu > table > tbody > tr:nth-child(2) .menuSuche
    {
        -moz-appearance: menulist-textfield; color: #000000;
    }
    
    .menuSuche > option
    {
        background-color: aliceblue; color: #000000;
    }
}

@-moz-document domain(campusquality.tum.de) {
    #menue_frame_key_icon > img
    {
        width: 71px; height: 25px;
        visibility: hidden;
        z-index: 1;        
    }

    #menue_frame_key_icon
    {
        background-image: url(https://wiki.tum.de/download/attachments/13829764/icon_key_normal_tumprod.png);
        background-repeat: no-repeat;
        display: block;
        z-index: 9;
    }

    #menue_frame_keyAn_icon > img
    {
        width: 71px; height: 25px;
        visibility: hidden;
        z-index: 1;
    }

    #menue_frame_keyAn_icon
    {
        background-image: url(https://wiki.tum.de/download/attachments/13829764/icon_keyan_normal_tumprod.png);
        background-repeat: no-repeat;
        display: block;
        z-index: 9;
    }
}
