css:
@-moz-document url('http://www.dreadcast.net/Main'), url-prefix('http://www.dreadcast.net/Main') {
/*       'DC Chat Enlarger Better Scrollbar' by .1019      */
/*                         DC Main                         */


#zone_chat ::-webkit-scrollbar {
    width: 8px /*Largeur de la barre de scroll. Pas recommandé de faire +10px*/;
}

#zone_chat ::-webkit-scrollbar:hover {
    width: 15px /*Si vous supprimer l'augmentation de la largeur de la barre du scroll lorsque vous passez la souris dessus, enlevez cette ligne ou écrivez "8px" au lieu de "15px"*/;
}

#zone_chat ::-webkit-scrollbar-track {
    -webkit-box-shadow: inset 0 0 6px rgba(0,0,0,0.3) /*Ombre du scroll, vous pouvez retirer si vous ne voulez voir que le truc à déplacer*/; 
    border-radius: 10px /*Faire de jolis bords arrondis hihihi*/;
}

#zone_chat ::-webkit-scrollbar-thumb {
    border-radius: 10px /*Faire de jolis bords arrondis :3 !!*/;
    -webkit-box-shadow: inset 0 0 6px rgba(0,0,0,0.5) /*Ombre dans la barre*/; 
    background: #153b54 /*Couleur de la barre: changer par la couleur souhaitée*/ ;
}
}
