css:
@-moz-document domain("pinterest.com") {
/* PINTEREST */

.Module.Nags {
    visibility: hidden;
    display: none;
}

.Modal.Module.absoluteCenter.show {
    visibility: hidden;
    display: none;
}

.Module.NagBase.NagNoScript {
    visibility: hidden;
    display: none;
}

.unauthbanner {
    visibility: hidden;
    display: none;
}

.FullPageModal__scroller {
    visibility: hidden;
    display: none;
}

div.NagSpacer { 
    display:none !important; 
}

div.hasFooter { 
    overflow-y: visible !important; 
}

div.Modal.fullScreen { 
    display: none !important; 
}

div.Nag, div.Nags { 
    display:none !important; 
}


html {
    overflow-y: scroll;
}

body {
    position: absolute;
}

.noScroll {
    overflow-y: scroll;
}
}

@-moz-document domain("facebook.com") {
/* FACEBOOK */

._5hn6 #u_0_f, #pagelet_growth_expanding_cta *{
    visibility: hidden!important;
    display: none!important;
}


.generic_dialog.pop_dialog.generic_dialog_modal#dialog_0 {
    visibility: hidden;
    display: none;
}
}

