css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("tatoeba.org") {

/* Revert to the smaller top navigation bar */

#top_menu {
    padding: 0px;
}
#logo .tatoeba-name {
    vertical-align: top;
    margin: 0 2px;
}
#languageSelectionContainer,
#user_menu {
    line-height: 32px;
}
#languageSelection {
    margin-top: 0px;
}
#profile img,
#log_out img,
#inbox img {
    top: 0px;
}
/* logo hidden, since I couldn't easily resize it */

#logo img {
    display: none;
}

}
