css:
@-moz-document domain("eloquentjavascript.net") {

body {
  background-color: #161616;
  color: #CECECE;
}

code, pre, .editor-wrap, .CodeMirror {
  font-family: 'Inconsolata', monospace !important;
  background-color: rgba(38, 41, 44, 1);
  color: rgba(248, 248, 242, 1);
}

.sandboxhint, .CodeMirror-linenumber, .CodeMirror-linenumbers {
  background-color: #373737;
}

.sandbox-open-menu > div {
  background-color: #373737;
  color: rgba(255, 255, 255, 0.9);
}

h1, h2, h3, h4, h5, h6, h7, p, blockquote, blockquote p, .sandboxhint, pre, pre > span {
  color: rgba(255, 255, 255, 0.9);
}

.cm-number {
  color: rgba(174, 129, 255, 1) !important;
}

.cm-atom {
  color: rgba(174, 129, 255, 1) !important;
}

.cm-keyword {
  font-style: italic;
  color: rgba(102, 217, 239, 1) !important;
}

.cm-variable {
  color: rgba(166, 226, 46, 1) !important;
}

.cm-variable-2 {
  color: rgba(248, 248, 242, 1) !important;
}

.cm-property {
  color: rgba(248, 248, 242, 1) !important;
}

.cm-comment {
  color: rgba(117, 113, 94, 1) !important;
}

.cm-string {
  color: rgba(230, 219, 116, 1) !important;
}

.cm-def {
  font-style: italic;
  color: rgba(253, 151, 31, 1) !important;
}

.cm-operator {
  color: rgba(255, 255, 255, 0.79) !important;
}

.cm-qualifier {
  color: rgba(166, 226, 46, 1) !important;
}

.cm-tag {
  color: rgba(248, 248, 242, 1) !important;
}

.cm-s-default .cm-variable-2, .cm-s-default .cm-attribute {
  color: rgba(248, 248, 242, 1) !important;
}

ol li::before {
  color: #CECEC6;
}

.CodeMirror-cursor {
  border-left: 1px solid white !important;
}

.CodeMirror-selected {
  background: rgba(73, 72, 62, 1) !important;
}
}
