css:
@-moz-document domain("vk.com") {
body {
    background: #fff;
    color: #000;
    margin: 0px;
    padding: 0px;
    direction: ltr;
    font-family: tahoma, arial, verdana, sans-serif, Lucida Sans;
    font-size: 11px;
    line-height: 1.182;
    font-weight: normal;
    -webkit-font-smoothing: subpixel-antialiased;
}

#side_bar .fl_l {
   font-size:11px;}

.page_body {
   font-size:11px;}

#page_body {
   font-size:11px;}

a:-webkit-any-link {
   font-size:11px;}

a {
   font-size:11px;}

.im-chat-input .im-chat-input--text {
   font-size:11px;}

.feed_wrap .explain {
   font-size:11px;}

#side_bar .left_label {
   font-size:11px;}

.left_list_info {
   font-size:11px;}

.wall_text {
    padding: 5px 0px 0px 4px;
    margin-left: -4px;
    -o-text-overflow: ellipsis;
    text-overflow: ellipsis;
    position: relative;
}
.wall_post_text {font-size:11px;}
.ui_rmenu_item, .ui_rmenu_subitem {font-size:11px;}
.wall_module .post {font-size:11px;}

   
.wall_module .copy_quote {
    border-left: 0px solid #dee6ee;
    padding-left: 0px;
    margin: 5px 0 0;
}
   

.post_author .author, .wall_module .author, .wall_module .copy_author {
   font-size:11px;}

.post_date, .post_date .post_link, .post_date .wall_text_name_explain_promoted_post {
   font-size:11px;}


.nim-dialog.nim-dialog_classic .nim-dialog--name, .nim-dialog.nim-dialog_classic .nim-dialog--preview, .nim-dialog.nim-dialog_classic .nim-dialog--text-preview {
   font-size:11px;}

.im-mess .im-mess--text {
   font-size:11px;}

.im-chat-input .im-chat-input--txt-wrap {
   font-size:11px;}
}
