css:
@-moz-document url-prefix("http://tree.tv"), url-prefix("http://www.tree.tv"), domain("tree.tv") {
/*
    Author: Oleksandr Vasyliev
    Version: 2
    © 2018
*/

body {
    background: #f1f1f1 !important;
}
.wrapper {
    margin-top: 0 !important;
}
.colorless {
    -webkit-filter: wave(0%) !important;
    -moz-filter: wave(0%) !important;
    -ms-filter: wave(0%) !important;
    -o-filter: wave(0%) !important;
    filter: wave(100%) !important;
}
.colorless *:not(.popup) {
    filter: none !important;
    -webkit-filter: grayscale(0%) !important;
    -moz-filter: grayscale(0%) !important;
    -ms-filter: grayscale(0%) !important;
    -o-filter: grayscale(0%) !important;
}
.main_content,
.main_content_item .accordion .accordion_item .accordion_head {
    width: 100% !important;
}
.main_content_item .accordion .accordion_item .accordion_head {
    background-image: none !important;
    background: rgba(255, 255, 255, 1) !important;
    background: -webkit-gradient(left top, left bottom, color-stop(0%, rgba(255, 255, 255, 1)), color-stop(100%, rgba(235, 235, 235, 1))) !important;
    background: -webkit-linear-gradient(top, rgba(255, 255, 255, 1) 0%, rgba(235, 235, 235, 1) 100%) !important;
    background: linear-gradient(to bottom, rgba(255, 255, 255, 1) 0%, rgba(235, 235, 235, 1) 100%) !important;
    box-shadow: 0px 0px 2px 1px rgba(0, 0, 0, 0.2) !important;
    margin-bottom: 10px;
}
#main_content_item_wrap {
    margin-left: -360px !important;
    left: 50% !important;
    position: relative !important;
}
#center_menu {
    margin-left: -365px !important;
    left: 50% !important;
    position: relative !important;
}
.add_comment {
    border-top: solid 1px #d1d1d1;
}
#brTop {
    display: none !important;
    background: #F5F5F5 !important;
}
.banners,
#banners {
    margin-top: -4175px
}
#br-link,
#b_pr_266,
.tLogo,
.traforet-logo,
#bannerscomment_wrap,
#TovarroScriptRootC572793,
.profileplus_link,
#profileplus_link,
.commercial_link,
#commercial_link,
#feedback_tab,
.main_content_item.un-height hr,
div[id^="epom"],
div[id^="admixer"],
div[id^="ambn"],
div[src^="criteo"],
object[data^="mxtads"],
#android_app_link,
.colorless_message,
#colorless_message,
.new_back_link,
.t_center,
noindex {
    display: none !important;
}
.paginationControl,
.give_more {
    margin-left: -50%;
}
}
