css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://forum.aquacomputer) {

* {
    background-color: #444444 !important;
    background-image: none !important;
    color: #dddddd !important;
    border-color: #f8c400 !important;

}
a:link {
    color: #F8C400 !important;
    text-decoration:none !important;
}
a:visited {
    color: #F8C400 !important;
    text-decoration:none !important;
}
a:hover,
a:active {
    color: #0093dd !important;
    text-decoration:none !important;
}
input[type=button],
input[type=submit],
input[type=reset] {
    background-color: #505050 !important;
    color: black !important;
    border-color: #202020 !important;
}

h2, h3
{font-weight: bold !important}

.columnReplies, .columnViews, .columnViews hot, #container, #mainspacer, .contentHeader, .columnIcon, #headernavi, #logoac, .mainHeadline, .pageNavigation, .breadCrumbs, .contentFooter, #main, .submenu, .active trail, .headlineContainer, #mainmenuext
{background-color:#272A2F !important;}

#logo, img, a, span, h2, h3, p, li, .pageOptions 
{background-color:transparent !important;}

}
