css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("zooqle.com") {
  
  
  
  /* Bye */
.col-md-12,
.navbar-inverse,
#sidebar {
    display: none !important;
}

/* #MakeEverythinFlatAgain */
border-left,
border-top,
border-right,
border-left,
border-top,
border-bottom,
border {
    display: none !important;
}
* {
    border-left: none !important;
    border-top: none !important;
    border-right: none !important;
    border-bottom: none !important;
    text-decoration: none !important;
    text-shadow: none !important;
    border-top: none !important;
}

/* Font */
* {
    font-family: "helvetica", helvetica, sanfrancisco, "sanfrancisco", "San Francisco Display Regular", '.System San Francisco Display', "SystemSanFranciscoDisplayRegular" !important;
}
@font-face {
    font-family: sanfrancisco;
    src: url(https://fonts.googleapis.com/css?family=Open+Sans);
    font-weight: normal;
    font-style: normal;
}

/* Beautiful */
#movbox,
.nav-tabs > li.active > a,
.nav-tabs > li.active > a:hover,
.nav-tabs > li.active > a:focus,
.list-group-item,
.form-control,
.table-torrents tr td,
text-nowrap,
.dropdown-menu,
div.modal-body,
#tvbox,
#body_container,
.navbar {
    background: #080808 !important;
    background-color: #080808 !important;
    color: #999999 !important;
    border-top: 1px dashed #000 !important;
}
.panel,
body,
div.panel-body,
.input-group-btn > button:nth-child(1),
#inputSearchTop,
.tt-hint,
#frmSearchTop > div:nth-child(1),
.btn-group > a:nth-child(1),
btn btn-xs,
#frmSearchTop {
    background: #080808 !important;
    background-color: #080808 !important;
    border-top: 1px dashed #000 !important;
}
.text-nowrap {
    background: none !important;
}
.col-md-9 {
    width: 100% !important;
}
.imgdiv img,
.cell img {
    opacity: 0.9 !important;
}
.txt-title.txt-title {
    color: #999999;
    font-weight: bold;
}
.rm-trigger:hover,
.cell:hover,
.nav-pills.compact > li.active > a,
.table-hover > tbody > tr:hover,
a:hover,
.col-md-12 {
    color: #808080 !important;
    background-color: #000 !important;
}
.table-objects tbody tr,
.inner,
.table-data tr:not(:first-child) {
    border-top: .5px dashed #000 !important;
}

/* Invert Trick */
#logo {
    -webkit-filter: invert(100%) !important;
    filter: invert(100%) !important;
}}
