css:
@-moz-document domain("openrec.tv") {
@font-face {
  font-family: 'Roboto';
  font-style: normal;
  font-weight: 400;
  src: local('Roboto'), local('Roboto-Regular'), url(https://fonts.gstatic.com/s/roboto/v15/CWB0XYA8bzo0kSThX0UTuA.woff2) format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215, U+E0FF, U+EFFD, U+F000;
}
body {
    font-family: 'Roboto', sans-serif;
    font-size: 1.4rem;
}
.c-chart .c-chart__list .c-chart__list__item .c-chart__list__item__name {
    display: inline-block;
    word-wrap: normal;
    word-break: normal;
}
.c-chart__list.js-chartAreaPc {
    background: #222;
    opacity: 0.8;
}
.c-chart.js-chartArea.live {
    background: url('http://orig00.deviantart.net/e831/f/2013/231/d/4/anime_girl_render___by_kyomo_san-d6ivn5k.png');
    background-size: contain;
    background-repeat: no-repeat;
    background-position: center;
}
}
