css:
@-moz-document domain("discuss.moe") {
html {
    background-color: #DFDFDF;
}
/*html broke panel fixing*/

html,
.user-main > .has-background > *,
#user-card > * {
    filter: invert(100%) hue-rotate(180deg);
    -webkit-filter: invert(100%) hue-rotate(180deg);
}
img,
video,
.ytp-thumbnail,
.lazyYT-image-loaded,
.user-main > .has-background,
#user-card,
iframe[src*="gfycat.com"],
iframe[src*="soundcloud.com"],
header.main-header {
    filter: invert(100%) hue-rotate(-180deg);
    -webkit-filter: invert(100%) hue-rotate(-180deg);
}
/*fix to position:fixed*/

#reply-control {
    position: sticky;
    top: 0px;
    z-index: 1000;
}
