css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("vk.com") {
  .im_msg_media_sticker, .im_sticker_row { height: 18px; width: 33%; overflow: hidden; margin: 0 auto; background-color: #EDF1F5; border-radius: 20px; padding: 8px; }
  .im_sticker_row > a { width: 0px; display: none; }
  .im_msg_media_sticker:hover:before, .im_sticker_row:hover:before { content: "Здесь был стикер"; display: block; color: #00745C; font-weight: 600; text-align: center; }
  .im_msg_media_sticker:hover, .im_sticker_row:hover { width: 80%; }
  .emoji_shop, .emoji_tabs_wrap { display: none; }
}
