css:
@-moz-document regexp("https://github.com/.*/pull.*") {
    .container {
        width: 1400px;
    }

    .discussion-timeline {
        width: 1190px;
    }
    
    .comment-holder {
        width: 100%;
        max-width: none;
    }
    
    .inline-comments .inline-comment-form-container {
        width: 100%;
        max-width: none;
    }
    
    .inline-comments .inline-comment-form {
        width: 100%;
        max-width: none;
    }
    
    .timeline-new-comment {
        max-width: none;
    }
}
