css:
@-moz-document domain(tumblr.com) {

#home_button.tab.iconic > a,
#home_button.tab.iconic.selected > a {
  background-image: url("http://i.imgur.com/t7h9tzl.png") !important;
  background-size: 37px 37px !important;
  background-repeat: no-repeat !important;
  opacity: .95 !important;
}
#home_button.tab.iconic > .tab_anchor::before {
	content: none !important;
}
#home_button a {

    height: 0 !important;
    width: 0 !important;
    top: 0px !important;
    padding-left: 36px !important;
    padding-top: 36px !important;
}

}
