css:
/* i really want this to be global */

.chat {

	/* PUT YOUR OWN BACKGROUND IMAGE IN THE QUOTES BELLOW */
  	
    background-image: 
	url('http://i0.kym-cdn.com/photos/images/original/000/615/155/ce9.gif') !important;
	
    /* PUT YOUR OWN BACKGROUND IMAGE IN THE QUOTES ABOVE */
    
    background-repeat: no-repeat !important;
    background-attachment: fixed !important;
    background-size: cover !important;
    background-position: center !important;
}
.message {
    background-color: rgba(11, 11, 11, 0.90) !important;
    padding-left: 9px;
    padding-right: 9px;
    padding-top: 9px;
    margin-left:10px;
    margin-right:10px;
    padding-bottom: 9px;
	margin-top:-11px;
    border-top:1px solid #212121;
	border-bottom-left-radius: 2.5px;
    border-bottom-right-radius: 2.5px;
    margin-bottom:10px !important;
}

.sakurako--dark .chat .message--first:not(:first-child) {
    margin-top: 10px;
    margin-left:10px;
    margin-right:10px;
	 margin-bottom:10px;
	border-top-left-radius: 2.5px;
    border-top-right-radius: 2.5px;
    padding-left: 9px !important;
    padding-right: 9px !important;
    padding-top: 9px !important;
    padding-bottom: 9.30px !important;
}
