css:
@-moz-document url-prefix("http://www.americanthinker.com/articles") {
body {
   background-color: #364264;
   font-family: "droid sans", verdana
}

/* working on home page: */
div.top_articles,
div.blog_container,
div.prev_articles_top  { background-color: lightgray !important; }

/* Article pages: */
div.column_610 {
    width: 665px !important
}                                            
div.column_610,
blockquote {
   background-color: lightgray; /* #aaccde;   brighter: 8ef */
   padding-left: 15px;
   color: black !important;
   font-family: "droid sans", verdana
}
div#wrapper main.article div.column_610 div.article_body_bottom {
   font-size: 10px !important;
   font-family: droid sans, verdana !important;
   font-weight: normal;
}
div.article_body {
   font-size: 14pt;
   font-family: droid sans, verdana
}
div.article_date,
h1   {
    color: navy
}                                            
}
