css:
@-moz-document domain("webchat.oftc.net") {
* {
    font-family: Monospace !important;
}
#ircui > .topboundpanel {
    background: #0d0309;
}
#ircui > .topboundpanel > div.tabbar > a.tab {
    background-color: #99f;
    color: #0d0309;
    border-color: #0d0309;
}
#ircui > .topboundpanel > div.tabbar > a.tab:first-child {
    border-top-left-radius: 20px;
    border-bottom-left-radius: 20px;
}
#ircui > .topboundpanel > div.tabbar > a.tab:last-child {
    border-top-right-radius: 20px;
    border-bottom-right-radius: 20px;
}
#ircui > .topboundpanel > div.tabbar > a.tab.tab-selected {
    background-color: #c9c;
    color: #0d0309;
}
#ircui > div.topic.tab-invisible.dynamicpanel.qwebirc-qui.widepanel.colourline {
    color: #ffffc6;
    background: #0d0309;
}
#ircui > .ircwindow {
    color: #ccf;
    background: #0d0309;
}
#ircui > div.nicklist.tab-invisible.dynamicpanel.qwebirc-qui,
#ircui > div.nicklist.tab-invisible.dynamicpanel.qwebirc-qui a {
    color: #ffcc99;
    background: #0d0309;
    border-color: #0d0309;
}
#ircui > .bottomboundpanel.input > form > input,
#ircui > .bottomboundpanel.input {
    background-color: #0d0309;
    color: #ffcc66;
    border-color: #ffcc66;
}
}
